import React from "react";
import AddCircleRoundedIcon from "@mui/icons-material/AddCircleRounded";
import CardGiftcardRoundedIcon from "@mui/icons-material/CardGiftcardRounded";
import EmojiEmotionsIcon from "@mui/icons-material/EmojiEmotions";
import GifIcon from "@mui/icons-material/Gif";
const chatareaMessage = () => {
  return (
    <div className="flex bg-[#474653] items-center">
      <AddCircleRoundedIcon fontSize="large" />
      <input className="bg-transparent p-5" placeholder={"Send Message"} />
      <button className="hidden">Send Message</button>
      <div className="flex justify-between">
        <CardGiftcardRoundedIcon fontSize="large" />
        <GifIcon fontSize="large" />
        <EmojiEmotionsIcon fontSize="large" />
      </div>
    </div>
  );
};

export default chatareaMessage;
